import React, { useState } from 'react';

const App = () => {
  const [selectedTab, setSelectedTab] = useState('assets');

  const handleTabChange = (tab) => {
    setSelectedTab(tab);
  };

  const tabs = ['assets', 'activity', 'send', 'browser', 'settings'];

  const tabButtonStyle = {
    padding: '10px 15px',
    borderRadius: '5px',
    margin: '0 5px',
    cursor: 'pointer',
    backgroundColor: '#0366d6',
    color: '#fff',
    border: 'none',
    outline: 'none',
  };

  const contentStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    fontFamily: 'Arial, sans-serif',
    padding: '20px',
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'center', backgroundColor: '#f6f6f6', padding: '10px' }}>
        {tabs.map(tab => (
          <button
            key={tab}
            onClick={() => handleTabChange(tab)}
            style={{
              ...tabButtonStyle,
              backgroundColor: selectedTab === tab ? '#0366d6' : '#ccc',
            }}
          >
            {tab}
          </button>
        ))}
      </div>
      <div style={contentStyle}>
        <h2>Content for {selectedTab} tab goes here</h2>
        {/* Здесь должно быть содержание для каждой вкладки */}
      </div>
    </div>
  );
};

export default App;
