import React, { useState } from 'react';

const App = () => {
  const [selectedTab, setSelectedTab] = useState('assets');
  const [nftTokens] = useState([
    { id: 1, name: 'NFT Token 1' },
    { id: 2, name: 'NFT Token 2' },
    { id: 3, name: 'NFT Token 3' },
    { id: 4, name: 'NFT Token 4' },
    { id: 5, name: 'NFT Token 5' },
  ]);
  const [tokens] = useState([
    { id: 1, name: 'Token 1' },
    { id: 2, name: 'Token 2' },
    { id: 3, name: 'Token 3' },
    { id: 4, name: 'Token 4' },
    { id: 5, name: 'Token 5' },
  ]);

  const handleTabChange = (tab) => {
    setSelectedTab(tab);
  };

  const tabs = ['assets', 'activity', 'send', 'browser', 'settings'];

  const tabButtonStyle = {
    padding: '10px 15px',
    borderRadius: '5px',
    margin: '0 5px',
    cursor: 'pointer',
    backgroundColor: '#0366d6',
    color: '#fff',
    border: 'none',
    outline: 'none',
  };

  const contentStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    fontFamily: 'Arial, sans-serif',
    padding: '20px',
    border: '2px solid #0366d6',
    borderRadius: '10px',
    backgroundColor: '#f6f6f6',
  };

  const balanceStyle = {
    fontSize: '24px',
    fontWeight: 'bold',
    margin: '20px 0',
  };

  const buttonContainerStyle = {
    display: 'flex',
    justifyContent: 'center',
    marginTop: '20px',
  };

  const buttonStyle = {
    padding: '10px 20px',
    borderRadius: '5px',
    margin: '0 10px',
    cursor: 'pointer',
    backgroundColor: '#0366d6',
    color: '#fff',
    border: 'none',
    outline: 'none',
  };

  const renderTokens = (tokenList) => {
    return tokenList.map(token => (
      <div key={token.id} style={{ margin: '5px 0' }}>
        {token.name}
      </div>
    ));
  };

  const handleNFTButtonClick = () => {
    // Логика для кнопки "Мои НФТ"
    setSelectedTab('nft');
  };

  const handleTokenButtonClick = () => {
    // Логика для кнопки "Мои Токены"
    setSelectedTab('tokens');
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', paddingTop: '20px' }}>
      <div style={{ width: '500px' }}>
        <div style={{ display: 'flex', justifyContent: 'center', backgroundColor: '#f6f6f6', padding: '10px', borderRadius: '10px 10px 0 0' }}>
          {tabs.map(tab => (
            <button
              key={tab}
              onClick={() => handleTabChange(tab)}
              style={{
                ...tabButtonStyle,
                backgroundColor: selectedTab === tab ? '#0366d6' : '#ccc',
              }}
            >
              {tab}
            </button>
          ))}
        </div>
        <div style={contentStyle}>
          <h2>Content for {selectedTab === 'assets' ? 'assets' : selectedTab} tab goes here</h2>
          {selectedTab === 'assets' && (
            <div style={balanceStyle}>
              Balance: 1000 ETH {/* Ваш баланс */}
            </div>
          )}
          {selectedTab === 'nft' && (
            <div>
              <h3>Мои НФТ</h3>
              {renderTokens(nftTokens)}
            </div>
          )}
          {selectedTab === 'tokens' && (
            <div>
              <h3>Мои Токены</h3>
              {renderTokens(tokens)}
            </div>
          )}
          <div style={buttonContainerStyle}>
            <button onClick={handleNFTButtonClick} style={buttonStyle}>Мои НФТ</button>
            <button onClick={handleTokenButtonClick} style={buttonStyle}>Мои Токены</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
