import React, { useState, useEffect } from "react";
import UCP from "./contracts/UCP.json";
import Web3 from "web3";
import "./App.css";

const App = () => {


  const [state, setState] = useState({
    web3: null,
    contract: null,
    newAccount: null,
  });

  useEffect(() => {
    const provider = new Web3.providers.HttpProvider(
      "http://localhost:5284/api/get-data"
    );

    async function initializeWeb3() {
      const web3 = new Web3(provider);
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = UCP.networks[networkId];

      const contract = new web3.eth.Contract(
        UCP.abi,
        deployedNetwork.address
      );

      setState({ web3, contract });
    }

    provider && initializeWeb3();
  }, []);


  const [selectedTab, setSelectedTab] = useState('assets');
  const [nftTokens] = useState([
    { id: 1, name: 'УЦП на уборку помещений', hash: '0x78db5c69f1dbb85c4e6fe0ca529d71a9773b4c409b11b89bf16f92695111199d' },
    { id: 2, name: 'NFT Token 2', hash: 'hash2' },
    { id: 3, name: 'NFT Token 3', hash: 'hash3' },
    { id: 4, name: 'NFT Token 4', hash: 'hash4' },
    { id: 5, name: 'NFT Token 5', hash: 'hash5' },
  ]);
  const [tokens] = useState([
    { id: 1, name: 'Token 1', hash: 'tokenhash1' },
    { id: 2, name: 'Token 2', hash: 'tokenhash2' },
    { id: 3, name: 'Token 3', hash: 'tokenhash3' },
    { id: 4, name: 'Token 4', hash: 'tokenhash4' },
    { id: 5, name: 'Token 5', hash: 'tokenhash5' },
  ]);

  const handleTabChange = (tab) => {
    setSelectedTab(tab);
  };

  const tabs = ['Главная', 'Расширения', 'Журнал', 'Транзакции', 'Настройки'];

  const tabButtonStyle = {
    padding: '10px 15px',
    borderRadius: '5px',
    margin: '0 5px',
    cursor: 'pointer',
    backgroundColor: '#0366d6',
    color: '#fff',
    border: 'none',
    outline: 'none',
  };

  const contentStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    fontFamily: 'Arial, sans-serif',
    padding: '20px',
    border: '2px solid #0366d6',
    borderRadius: '10px',
    backgroundColor: '#f6f6f6',
  };

  const balanceStyle = {
    fontSize: '24px',
    fontWeight: 'bold',
    margin: '20px 0',
  };

  const tokenListStyle = {
    textAlign: 'left',
    marginTop: '10px',
  };

  const tokenItemStyle = {
    border: '1px solid #ccc',
    borderRadius: '5px',
    padding: '10px',
    marginBottom: '5px',
  };


  const getData = async () => {
    const { contract } = state;

    try {
      const ucpRecords = await contract.methods.getUCPs("0x4bCBFBD0FAd8dcaB77bEBf54134Fa66Ef0717D2a").call();
      return ucpRecords;
    } catch (error) {
      console.error('Ошибка при получении данных:', error);
      return []; // Возвращаем пустой массив или другое значение по умолчанию в случае ошибки
    }
  };

  const renderTokens = (tokenList) => {

    return tokenList.map(token => (
      <div key={token.id} style={tokenItemStyle}>
        <div><b>{token.name}</b></div>
        <div>Хэш токена: {token.hash}</div>
        <button>Детали УЦП</button>
      </div>
    ));
  };

  const handleNFTButtonClick = () => {
    setSelectedTab('nft');
  };

  const handleTokenButtonClick = () => {
    setSelectedTab('tokens');
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', paddingTop: '20px' }}>
      <div style={{ width: '800px' }}>
        <div style={{ display: 'flex', justifyContent: 'center', backgroundColor: '#f6f6f6', padding: '10px', borderRadius: '10px 10px 0 0' }}>
          {tabs.map(tab => (
            <button
              key={tab}
              onClick={() => handleTabChange(tab)}
              style={{
                ...tabButtonStyle,
                backgroundColor: selectedTab === tab ? '#0366d6' : '#ccc',
              }}
            >
              {tab}
            </button>
          ))}
        </div>
        <div style={contentStyle}>
          <h2>Content for {selectedTab === 'assets' ? 'assets' : selectedTab} tab goes here</h2>
          <div style={balanceStyle}>
            Баланс: 100 UBT {/* Ваш баланс */}
          </div>
          <div style={tokenListStyle}>
            {selectedTab === 'nft' && (
              <>
                <h3>Мои УЦП</h3>
                {renderTokens(nftTokens)}
              </>
            )}
            {selectedTab === 'tokens' && (
              <>
                <h3>Мои ЦФА</h3>
                {renderTokens(tokens)}
              </>
            )}
          </div>
          <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
            <button onClick={handleNFTButtonClick} style={tabButtonStyle}>Мои УЦП</button>
            <button style={tabButtonStyle}>Мои ЦФА</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
