import React, { useState, useEffect } from "react";
import UCP from "./contracts/UCP.json";
import Web3 from "web3";
import "./App.css";

const App = () => {
  const [state, setState] = useState({
    web3: null,
    contract: null,
    newAccount: null,
  });

  useEffect(() => {
    const provider = new Web3.providers.HttpProvider(
      "http://localhost:5284/api/get-data"
    );

    async function initializeWeb3() {
      const web3 = new Web3(provider);
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = UCP.networks[networkId];

      const contract = new web3.eth.Contract(
        UCP.abi,
        deployedNetwork.address
      );

      setState({ web3, contract });
    }

    provider && initializeWeb3();
  }, []);

  const [selectedTab, setSelectedTab] = useState('assets');
  const [ucpRecords, setUcpRecords] = useState([]);

  const handleTabChange = (tab) => {
    setSelectedTab(tab);
  };

  const tabs = ['Главная', 'Расширения', 'Журнал', 'Транзакции', 'Настройки'];

  const tabButtonStyle = {
    padding: '10px 15px',
    borderRadius: '5px',
    margin: '0 5px',
    cursor: 'pointer',
    backgroundColor: '#0366d6',
    color: '#fff',
    border: 'none',
    outline: 'none',
  };

  const contentStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    fontFamily: 'Arial, sans-serif',
    padding: '20px',
    border: '2px solid #0366d6',
    borderRadius: '10px',
    backgroundColor: '#f6f6f6',
  };

  const balanceStyle = {
    fontSize: '24px',
    fontWeight: 'bold',
    margin: '20px 0',
  };

  const tokenListStyle = {
    textAlign: 'left',
    marginTop: '10px',
  };

  const tokenItemStyle = {
    border: '1px solid #ccc',
    borderRadius: '5px',
    padding: '10px',
    marginBottom: '5px',
  };

  const getData = async () => {
    const { contract } = state;

    try {
      const ucpRecords = await contract.methods.getUCPs("0xe5579765b92002904Ed838847DA3122DD60Ee98e").call();
      console.log(ucpRecords);
      setUcpRecords(ucpRecords);
    } catch (error) {
      console.error('Problem has been detected:', error);
    }
  };

  const handleNFTButtonClick = async () => {
    setSelectedTab('nft');
    await getData();
  };

  const handleTokenButtonClick = async () => {

    await getData();
  };

  const renderTokens = (tokenList) => {
    return tokenList.map((token, index) => (
      <div key={index} style={tokenItemStyle}>
        <div><b>Адрес контракта:</b> 0x4093a4540790291830898e337F62b8FA96fd2e0A</div>
        <div><b>Хэш-идентификатор УЦП:</b> 0x1583a454079029120898e337Faa96fd432e0A</div>
        <br/>
        <div><b>Наименование УЦП:</b> {token.ucpName}</div>
        <div><b>Описание УЦП:</b> {token.ucpDescription}</div>
        <div><b>Подтверждение:</b> {token.confirmed ? 'Подтверждено' : 'Ожидает подтверждения'}</div>
        <br/>
        <button>Детали УЦП</button>
        <br/>
        <button>Посмотреть код смарт-контракта</button>
        <br/>
        <button>Заключить смарт-контракт на данное УЦП</button>
      </div>
    ));
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', paddingTop: '20px' }}>
      <div style={{ width: '800px' }}>
        <div style={{ display: 'flex', justifyContent: 'center', backgroundColor: '#f6f6f6', padding: '10px', borderRadius: '10px 10px 0 0' }}>
          {tabs.map(tab => (
            <button
              key={tab}
              onClick={() => handleTabChange(tab)}
              style={{
                ...tabButtonStyle,
                backgroundColor: selectedTab === tab ? '#0366d6' : '#ccc',
              }}
            >
              {tab}
            </button>
          ))}
        </div>
        <div style={contentStyle}>
          <h2>Content for {selectedTab === 'assets' ? 'assets' : selectedTab} tab goes here</h2>
          <div style={balanceStyle}>
            Баланс: 100 UBT {/* Ваш баланс */}
          </div>
          <div style={tokenListStyle}>
            {selectedTab === 'nft' && (
              <>
                <h2>Мои УЦП</h2>
                {renderTokens(ucpRecords)}
              </>
            )}
            {selectedTab === 'tokens' && (
              <>
                <h3>Мои ЦФА</h3>

              </>
            )}
          </div>
          <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
            <button onClick={handleNFTButtonClick} style={tabButtonStyle}>Мои УЦП</button>
            <button onClick={handleTokenButtonClick} style={tabButtonStyle}>Мои ЦФА</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
