import React, { useState } from 'react';

const App = () => {
  const [selectedTab, setSelectedTab] = useState('ucp');

  const handleTabChange = (tab) => {
    setSelectedTab(tab);
  };

  const tokens = {
    ucp: [
      { logo: 'URL_ЛОГОТИПА_УЦП_1', hash: 'ХЭШ_УЦП_1' },
      // Добавьте другие УЦП по вашему выбору
    ],
    cfa: [
      { logo: 'URL_ЛОГОТИПА_ЦФА_1', hash: 'ХЭШ_ЦФА_1' },
      // Добавьте другие ЦФА по вашему выбору
    ],
  };

  const walletStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100vh',
    fontFamily: 'Arial, sans-serif',
  };

  const tabButtonStyle = {
    margin: '5px',
    backgroundColor: '#0366d6',
    color: '#fff',
    border: 'none',
    padding: '8px 16px',
    borderRadius: '4px',
    cursor: 'pointer',
  };

  return (
    <div style={walletStyle}>
      <div style={{ marginBottom: '20px' }}>
        <button
          onClick={() => handleTabChange('ucp')}
          style={{
            ...tabButtonStyle,
            backgroundColor: selectedTab === 'ucp' ? '#0366d6' : '#ccc',
          }}
        >
          Мои УЦП
        </button>
        <button
          onClick={() => handleTabChange('cfa')}
          style={{
            ...tabButtonStyle,
            backgroundColor: selectedTab === 'cfa' ? '#0366d6' : '#ccc',
          }}
        >
          Мои ЦФА
        </button>
      </div>
      {tokens[selectedTab].map((token, index) => (
        <div key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
          <div style={{
            width: '50px',
            height: '50px',
            borderRadius: '50%',
            backgroundColor: '#ccc',
            marginRight: '15px',
          }}>
            <img src={token.logo} alt="Token Logo" style={{ width: '100%', height: '100%', borderRadius: '50%' }} />
          </div>
          <div>
            <p>Hash: {token.hash}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default App;
