import React, { useState } from 'react';

const App = () => {
  const [selectedTab, setSelectedTab] = useState('assets');

  const handleTabChange = (tab) => {
    setSelectedTab(tab);
  };

  const tabs = ['assets', 'activity', 'send', 'browser', 'settings'];

  const tabButtonStyle = {
    padding: '10px 15px',
    borderRadius: '5px',
    margin: '0 5px',
    cursor: 'pointer',
    backgroundColor: '#0366d6',
    color: '#fff',
    border: 'none',
    outline: 'none',
  };

  const contentStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    fontFamily: 'Arial, sans-serif',
    padding: '20px',
  };

  const balanceStyle = {
    fontSize: '24px',
    fontWeight: 'bold',
    margin: '20px 0',
  };

  const buttonContainerStyle = {
    display: 'flex',
    justifyContent: 'center',
    marginTop: '20px',
  };

  const buttonStyle = {
    padding: '10px 20px',
    borderRadius: '5px',
    margin: '0 10px',
    cursor: 'pointer',
    backgroundColor: '#0366d6',
    color: '#fff',
    border: 'none',
    outline: 'none',
  };

  const handleNFTButtonClick = () => {
    // Логика для кнопки "Мои НФТ"
  };

  const handleTokenButtonClick = () => {
    // Логика для кнопки "Мои Токены"
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'center', backgroundColor: '#f6f6f6', padding: '10px' }}>
        {tabs.map(tab => (
          <button
            key={tab}
            onClick={() => handleTabChange(tab)}
            style={{
              ...tabButtonStyle,
              backgroundColor: selectedTab === tab ? '#0366d6' : '#ccc',
            }}
          >
            {tab}
          </button>
        ))}
      </div>
      <div style={contentStyle}>
        <h2>Content for {selectedTab} tab goes here</h2>
        <div style={balanceStyle}>
          Balance: 1000 ETH {/* Ваш баланс */}
        </div>
        <div style={buttonContainerStyle}>
          <button onClick={handleNFTButtonClick} style={buttonStyle}>Мои НФТ</button>
          <button onClick={handleTokenButtonClick} style={buttonStyle}>Мои Токены</button>
        </div>
      </div>
    </div>
  );
};

export default App;
