import React, { useState } from 'react';

const App = () => {
  const [selectedTab, setSelectedTab] = useState('ucp');

  const handleTabChange = (tab) => {
    setSelectedTab(tab);
  };

  const tokens = {
    ucp: [
      { logo: 'URL_ЛОГОТИПА_УЦП_1', hash: 'ХЭШ_УЦП_1' },
      // Добавьте другие УЦП по вашему выбору
    ],
    cfa: [
      { logo: 'URL_ЛОГОТИПА_ЦФА_1', hash: 'ХЭШ_ЦФА_1' },
      // Добавьте другие ЦФА по вашему выбору
    ],
  };

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      paddingTop: '20px',
    }}>
      <h2>Мои ЦФА и УЦП</h2>
      <div style={{ marginBottom: '20px' }}>
        <button
          onClick={() => handleTabChange('ucp')}
          style={{
            marginRight: '10px',
            backgroundColor: selectedTab === 'ucp' ? '#0366d6' : '#ccc',
            color: selectedTab === 'ucp' ? '#fff' : '#000',
            border: 'none',
            padding: '8px 16px',
            borderRadius: '4px',
            cursor: 'pointer',
          }}
        >
          Мои УЦП
        </button>
        <button
          onClick={() => handleTabChange('cfa')}
          style={{
            backgroundColor: selectedTab === 'cfa' ? '#0366d6' : '#ccc',
            color: selectedTab === 'cfa' ? '#fff' : '#000',
            border: 'none',
            padding: '8px 16px',
            borderRadius: '4px',
            cursor: 'pointer',
          }}
        >
          Мои ЦФА
        </button>
      </div>
      {tokens[selectedTab].map((token, index) => (
        <div key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
          <div style={{
            width: '50px',
            height: '50px',
            borderRadius: '50%',
            backgroundColor: '#ccc',
            marginRight: '15px',
          }}>
            <img src={token.logo} alt="Token Logo" style={{ width: '100%', height: '100%', borderRadius: '50%' }} />
          </div>
          <div>
            <p>Hash: {token.hash}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default App;
