import React, { useState } from 'react';
import Web3Connect from "./Web3Connect";
import '../css/Authorization.css';

const Authorization = () => {
  const { web3, contract } = Web3Connect();

  const [seedPhrase, setSeedPhrase] = useState('');
  const [authorized, setAuthorized] = useState(false);

  const handleSeedPhraseChange = (e) => {
    setSeedPhrase(e.target.value);
  };

  const handleAuthorization = () => {
    // Здесь можно выполнить проверку или обработку seed-фразы
    // Например, проверка длины или отправка на сервер для проверки

    // В данном примере, просто устанавливаем состояние authorized в true
    setAuthorized(true);
  };

  const getAddressByPrivateKey = (privateKey) => {
    const web3 = new Web3(new Web3.providers.HttpProvider('https://mainnet.infura.io/v3/YourInfuraApiKey'));

    // Проверяем длину приватного ключа
    if (!privateKey || privateKey.length !== 66) {
        throw new Error('Invalid private key');
    }

    // Создаем аккаунт из приватного ключа
    const account = web3.eth.accounts.privateKeyToAccount(privateKey);

    // Возвращаем адрес кошелька
    return account.address;
  }

  return (
    <div className="authorization-container">
      <h2>Авторизация (demo)</h2>
      {!authorized ? (
        <form className="authorization-form" onSubmit={handleAuthorization}>
          <label>
            Пожалуйста, введите приватный ключ для авторизации в кошельке:
            <input
              type="text"
              value={seedPhrase}
              onChange={handleSeedPhraseChange}
              required
            />
          </label>
          <button type="submit">Авторизоваться</button>
        </form>
      ) : (
        <div>
          <p>Successfully authorized!</p>
          {/* Здесь можно отображать защищенный контент */}
        </div>
      )}
    </div>
  );
};

export default Authorization;
