// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;
pragma experimental ABIEncoderV2;

contract UCP {
    address public admin;

    struct UCPRecord {
        string ucpName;
        string ucpDescription;
        bool confirmed;
    }
    
    mapping(address => UCPRecord[]) public ucpRecords;
    
    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can perform this operation");
        _;
    }
    
    function setUCP(address user, string memory _ucpName, string memory _ucpDescription) public {
        ucpRecords[user].push(UCPRecord(_ucpName, _ucpDescription, false));
    }
    
    function getUCPs(address user) public view returns (UCPRecord[] memory) {
        return ucpRecords[user];
    }
    
    function confirmUCP(address user, uint256 index) public onlyAdmin {
        require(index < ucpRecords[user].length, "Invalid index");

        UCPRecord storage record = ucpRecords[user][index];
        require(!record.confirmed, "UCP already confirmed");

        record.confirmed = true;
    }
}
